import Body from  '../Index'
import Navbar from '../../components/navbar/Navbar';
import { useEffect, useState } from 'react';
import axios from 'axios';


export default function Movies({i}) {

    const [films, setFilms] = useState([])

    useEffect(() => {
        axios.get(`https://swapi.dev/api/films/1/?format=json`).then(res => {
                setFilms(res.data.results( movie => (
                    {
                        title: movie.title,
                        text: movie.opening_crawl,
                        peoples: movie.characters
                    }
                )))
            })
        }, [])

    return (
        <Body>     
            <Navbar films={films}/>
            <p>{films.text}</p>
        </Body>
    );
};
