import user_friends from "../../assents/icons/user-friends.svg"
import user_circle from "../../assents/icons/user-circle.svg"
import map from "../../assents/icons/map-marker-alt.svg"
import Carousel from "react-elastic-carousel"
import "./Characters.css"
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Characters({people}) {

    const breakPoints = [
        { width: 1 , itemsToShow: 1},
        { width: 500 , itemsToShow: 4},

    ];

    const [local, setLocal] = useState([])
    useEffect(() => {
        axios.get(people.location).then(res => {
            setLocal(res.data.map( local => (
                    {
                        name: local.name,

                    }
                )))
            })
        }, [])

    console.log('local', local)


    return (
        <div className="row">

            <h2 className="title">  <img className="user_friends" src={user_friends}/> Characters</h2>

            <Carousel breakPoints={breakPoints}>
            {people.map( people =>  
                <div>
                    <div className="card center">
                        <div className="card-content">
                            <img className="user_circle" src={user_circle }/>  
                            <span className="card-title">{people.name}</span>
                            <img className="map" src={map}/>
                            <p className="card-information">{local.name}</p>
                        </div>
                    </div>
                </div> 
            )}
            </Carousel>
        </div>
    );
};
