import default_image from "../../assents/images/default.svg"
import star from "../../assents/icons/star.svg"
import chair_director from "../../assents/icons/chair-director-icon.svg"
import calendar from "../../assents/icons/calendar-icon.svg"
import "./Movies.css"
import Carousel from "react-elastic-carousel"


const breakPoints = [
    { width: 1 , }
];

export default function Movies({films}) {
    return (
        <div className="row main-gallery">


            <h2 className="title">  <img className="star" src={star}/> Movies</h2>
            <Carousel breakPoints={breakPoints}>
            {films.map( movie =>  
                    <div className="col s12 m4" gallery-cell>
                        <div className="card">
                            <div className="card-image">
                                <img src={default_image}/>  
                                <div className="line"/>
                            </div>
                            <div className="card-content">
                                <span className="card-title">{movie.title}</span>
                                <p className="card-text">{movie.text}</p>
                            </div>
                            <div class="card-content">
                                <p className="card-information"><img className="icon" src={chair_director}/>{movie.director}</p>
                                <p className="card-information"><img className="icon" src={calendar}/>{movie.date}</p>
                            </div>
                        </div>
                    </div>  
            )}
            </Carousel>
        </div>

    );  
};
