import Body from  '../Index'
import Navbar from '../../components/navbar/Navbar';
import Characters from '../../components/characters/Characters';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Movies() {

    const [films, setFilms] = useState([])
    const [people, setPeople] = useState([])


    const id = id.

     useEffect(async() => {
        await axios.get(`https://swapi.dev/api/films/${id}/?format=json`).then(res => {
                setFilms(res.data)    
            })
        }, [])


      //falta criar filtro  
        useEffect(() => {
            axios.get('https://swapi.dev/api/people/?format=json').then(res => {
                    setPeople(res.data.results.map( people => (
                        {
                            name: people.name,
                            location: people.homeworld + "/?format=json"
                        }
                    )))
                })
            }, [])



    return (
        <Body>     
            <Navbar films={films}/>

            <div className="col l8 push-l2 col s12" >
                <p>{films.opening_crawl}</p>
            </div>

            <Characters people={people}/>
        </Body>
    );
};
