import Body from  '../Index'
import Navbar from '../../components/navbar/Navbar';
import Movies from '../../components/movies/Movies'
import Characters from '../../components/characters/Characters';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Home() {

    const [people, setPeople] = useState([])
    const [films, setFilms] = useState([])

    useEffect(() => {
        axios.get('https://swapi.dev/api/films/?format=json').then(res => {
                setFilms(res.data.results.map( movie => (
                    {
                        title: movie.title,
                        text: movie.opening_crawl,
                        director: movie.director,
                        date: movie.release_date
                    }
                )))
            })
        }, [])

    useEffect(() => {
        axios.get('https://swapi.dev/api/people/?format=json').then(res => {
                setPeople(res.data.results.map( people => (
                    {
                        name: people.name,
                        location: axios.get(`${people.homeworld}/?format=json`).d
                    }
                )))
            })
        }, [])

    return (
        <Body>        
            <Navbar/>
            <Movies films={films}/>
            <Characters people={people}/>
        </Body>
    );
};
